pragma solidity ^0.4.19;

interface RandomBeacon {
    function getRandomByEpochId(uint256 epochId) external view returns(uint256);
    function getRandomByBlockTime(uint256 blockTime) external view returns(uint256);
    function getEpochId(uint256 blockTime) external view returns(uint256);
}

contract RbTest {
    uint256 public r;
    uint256 public r1;
    uint256 public r2;
    uint256 public epochID;

    function getRandomByEpochId(uint256 epochId) public view returns(uint256) {
//        uint256 p = 0;
//        uint len = 32;
//        uint ptr;
//        bytes4 sig = bytes4(keccak256("getRandomByEpochId(uint256"));
//        assembly {
//            mstore(0, 0x7f07b9ab00000000000000000000000000000000000000000000000000000000)
//            calldatacopy(4, 4, 32)
//            if not(iszero(staticcall(gas, 0x262, 0, 0x24, p, 0x20))) {
//                p := mload(0)
//            }
//        }
//        return p;
        assembly {
            let ptr := mload(0x40)
            calldatacopy(ptr, 0, calldatasize)
            let result := delegatecall(gas, 0x262, ptr, calldatasize, 0, 0)
            let size := returndatasize
            returndatacopy(ptr, 0, size)

            switch result
            case 0 { revert(ptr, size) }
            default { return(ptr, size) }
        }
    }
    function getRandomByBlockTime(uint256 blockTime) public view returns(uint256) {
        // RandomBeacon rb = RandomBeacon(0x0000000000000000000000000000000000000262);
        // return rb.getRandomByBlockTime(blockTime);
        uint256 p = 0;
        assembly {
            mstore(0, 0xdf39683800000000000000000000000000000000000000000000000000000000)
            calldatacopy(4, 4, 32)
            if not(iszero(staticcall(gas, 0x262, 0, 0x24, p, 0x20))) {
                p := mload(0)
            }
        }
        return p;
    }
    function getEpochId(uint256 blockTime) public view returns(uint256) {
        // RandomBeacon rb = RandomBeacon(0x0000000000000000000000000000000000000262);
        // return rb.getEpochId(blockTime);
        uint256 p = 0;
        assembly {
            mstore(0, 0x5303548b00000000000000000000000000000000000000000000000000000000)
            calldatacopy(4, 4, 32)
            if not(iszero(staticcall(gas, 0x262, 0, 0x24, p, 0x20))) {
                p := mload(0)
            }
        }
        return p;
    }

    function callGetRandomByEpochId(uint256 e) public view returns(uint256) {
        return getRandomByEpochId(e);
    }

    function sendRandomByBlockTime(uint256 blockTime) public {
        r = getRandomByBlockTime(blockTime);
        epochID = getEpochId(blockTime);
        r1 = callGetRandomByEpochId(epochID);
    }
}

